  socket.on("new_message", (msg) => {
    // your message handler...
  });

  socket.on("status_updated", (updatedMsg) => {
    // your status handler...
  });

  socket.on("user_typing", (wa_id) => {
    if (selectedChat?.wa_id === wa_id) {
      setTyping(true);
      setTimeout(() => setTyping(false), 1500);
    }
  });

  return () => {
