import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import api from '../services/api'
import { useAuth } from '../context/AuthContext'

function HomeFeed() {
  const [posts, setPosts] = useState([])
  const [text, setText] = useState('')
  const { user } = useAuth()

  useEffect(() => {
    fetchPosts()
  }, [])

  const fetchPosts = async () => {
    try {
      const res = await api.get('posts')
      setPosts(res.data)
    } catch (err) {
      console.error(err)
    }
  }

  const handleSubmit = async e => {
    e.preventDefault()
    try {
      const res = await api.post('posts', { text })
      setPosts([res.data, ...posts])
      setText('')
    } catch (err) {
      console.error(err)
    }
  }

  return (
    <div style={{ maxWidth: 600, margin: '2rem auto' }}>
      <h2>Welcome, {user.name}</h2>
      <button
        onClick={() => {
          window.location.href = `/profile/${user.id}`
        }}
      >
        View My Profile
      </button>
      <form
        onSubmit={handleSubmit}
        style={{ display: 'flex', flexDirection: 'column', margin: '1rem 0' }}
      >
        <textarea
          value={text}
          onChange={e => setText(e.target.value)}
          placeholder="What's on your mind?"
          required
          style={{ padding: '0.5rem', marginBottom: '0.5rem', height: '80px' }}
        />
        <button type="submit">Post</button>
      </form>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {posts.map(p => (
          <li
            key={p._id}
            style={{ borderBottom: '1px solid #ccc', padding: '1rem 0' }}
          >
            <Link to={`/profile/${p.user._id}`}>
              <strong>{p.user.name}</strong>
            </Link>{' '}
            <em>{new Date(p.createdAt).toLocaleString()}</em>
            <p>{p.text}</p>
          </li>
        ))}
      </ul>
    </div>
  )
}

export default HomeFeed
