import React, { useEffect, useState } from "react";
import axios from "axios";
import ChatWindow from "./components/ChatWindow";
import "./App.css"; // ✅ Import the CSS
import { io } from "socket.io-client";

const socket = io("http://localhost:5000");

function App() {
  const [chats, setChats] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);
  const [newMessage, setNewMessage] = useState("");

  useEffect(() => {
    fetchMessages();
    socket.on("new_message", (msg) => {
  setChats((prevChats) => {
    const updated = [...prevChats];
    const existing = updated.find(c => c.wa_id === msg.wa_id);

    if (existing) {
      const alreadyExists = existing.messages.some(
        (m) => m.meta_msg_id === msg.meta_msg_id
      );

      if (!alreadyExists) {
        existing.messages.push(msg);
      }
    } else {
      updated.push({
        wa_id: msg.wa_id,
        name: msg.name,
        number: msg.number,
        messages: [msg]
      });
    }

    return [...updated];
  });
});


socket.on("status_updated", (updatedMsg) => {
    setChats((prevChats) => {
      return prevChats.map(chat => {
        if (chat.wa_id === updatedMsg.wa_id) {
          const newMessages = chat.messages.map(msg =>
            msg.meta_msg_id === updatedMsg.meta_msg_id ? { ...msg, status: updatedMsg.status } : msg
          );
          return { ...chat, messages: newMessages };
        }
        return chat;
      });
    });
  });



    return () => {
      socket.off("new_message");
      socket.off("status_updated");
    };
  }, []);

  const fetchMessages = () => {
    axios.get("http://localhost:5000/webhook/messages")
      .then((res) => setChats(res.data))
      .catch(console.error);
  };

  const handleSend = async () => {
  if (!newMessage || !selectedChat) return;

  const payload = {
    type: "message",
    wa_id: selectedChat.wa_id,
    name: selectedChat.name,
    number: selectedChat.number,
    message: newMessage,
    timestamp: new Date().toISOString(),
    meta_msg_id: "msg_" + Date.now()
  };

  try {
    await axios.post("http://localhost:5000/webhook/receive", payload);
    setNewMessage(""); // Clear input

  } catch (err) {
    console.error("Send failed:", err);
  }
};

  return (
    <div className="app-container">
      <div className="sidebar">
        {chats.map((chat, i) => (
          <div
            key={i}
            className={`chat-item ${selectedChat?.wa_id === chat.wa_id ? "selected" : ""}`}
            onClick={() => setSelectedChat(chat)}
          >
            <strong>{chat.name}</strong><br />
            <small>{chat.number}</small>
          </div>
        ))}
      </div>
      <div className="chat-window">
        {selectedChat ? (
          <>
            <div className="chat-header">
              <strong>{selectedChat.name}</strong><br />
              <small>{selectedChat.number}</small>
            </div>
            <div className="chat-messages">
              <ChatWindow chat={selectedChat} />
            </div>
            <div className="send-box">
              <input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Type your message..."
              />
              <button onClick={handleSend}>Send</button>
            </div>
          </>
        ) : (
          <div className="chat-messages" style={{ padding: "20px" }}>
            <p>Select a chat</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
