import React, { useEffect, useState } from "react";
import axios from "axios";
import ChatWindow from "./components/ChatWindow";

function App() {
  const [chats, setChats] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);

  useEffect(() => {
    axios.get("http://localhost:5000/webhook/messages").then((res) => {
      setChats(res.data);
    });
  }, []);

  return (
    <div style={{ display: "flex" }}>
      <div style={{ width: "30%", borderRight: "1px solid #ccc", height: "100vh", overflowY: "scroll" }}>
        {chats.map((chat, index) => (
          <div key={index} onClick={() => setSelectedChat(chat)} style={{ padding: "10px", cursor: "pointer" }}>
            <strong>{chat.name}</strong> ({chat.number})
          </div>
        ))}
      </div>
      <div style={{ width: "70%" }}>
        {selectedChat ? <ChatWindow chat={selectedChat} /> : <p style={{ padding: "20px" }}>Select a chat</p>}
      </div>
    </div>
  );
}

export default App;
