import axios from "axios";
import { useEffect, useRef } from "react";

const ChatWindow = ({ chat }) => {
  const scrollRef = useRef(null);

  useEffect(() => {
    scrollRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [chat.messages]);

  const formatTime = (isoTime) => {
    const date = new Date(isoTime);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const handleDelete = async (meta_msg_id) => {
    try {
      await axios.delete(`http://localhost:5000/webhook/delete/${meta_msg_id}`);
    } catch (err) {
      console.error("Failed to delete:", err);
    }
  };

  return (
    <>
      {chat.messages.map((msg, index) => (
        <div
          key={index}
          className={`message-bubble ${msg.direction === "outgoing" ? "you" : "other"}`}
        >
          <div style={{ display: "flex", justifyContent: "space-between" }}>
            <div>{msg.message}</div>
            <span
              style={{ cursor: "pointer", color: "red", marginLeft: "10px" }}
              onClick={() => handleDelete(msg.meta_msg_id)}
              title="Delete message"
            >
              üóëÔ∏è
            </span>
          </div>
          <div className="timestamp">
            <span className="meta">
              {formatTime(msg.timestamp)}{" "}
              <span style={{ color: msg.status === "read" ? "blue" : "gray" }}>
                {msg.status}
              </span>
            </span>
          </div>
        </div>
      ))}
      <div ref={scrollRef} />
    </>
  );
};

export default ChatWindow;
