import React, { useEffect, useState } from "react";
import axios from "axios";
import ChatWindow from "./components/ChatWindow";
import {io} from "socket.io-client";
function App() {
  const [chats, setChats] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);
  const [newMessage, setNewMessage] = useState("");
  const socket = io("http://localhost:5000");
  // Fetch messages from backend
  const fetchMessages = () => {
    axios.get("http://localhost:5000/webhook/messages")
      .then((res) => setChats(res.data))
      .catch((err) => console.error("Failed to fetch:", err));
  };

  useEffect(() => {
    fetchMessages();
  }, []);

  // Handle send message
  const handleSend = async () => {
    if (!newMessage || !selectedChat) return;

    const payload = {
      type: "message",
      wa_id: selectedChat.wa_id,
      name: selectedChat.name,
      number: selectedChat.number,
      message: newMessage,
      timestamp: new Date().toISOString(),
      meta_msg_id: "msg_" + Date.now()
    };

    try {
      await axios.post("http://localhost:5000/webhook/receive", payload);
      setNewMessage("");
      fetchMessages(); // Refresh chat
    } catch (error) {
      console.error("Send error:", error);
    }
  };

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      {/* Sidebar */}
      <div style={{ width: "30%", borderRight: "1px solid #ccc", overflowY: "auto" }}>
        {chats.map((chat, index) => (
          <div key={index} onClick={() => setSelectedChat(chat)} style={{ padding: "10px", cursor: "pointer", background: selectedChat?.wa_id === chat.wa_id ? "#f0f0f0" : "" }}>
            <strong>{chat.name}</strong> ({chat.number})
          </div>
        ))}
      </div>

      {/* Chat Window */}
      <div style={{ width: "70%", display: "flex", flexDirection: "column" }}>
        {selectedChat ? (
          <>
            <div style={{ flex: 1, overflowY: "auto", padding: "20px" }}>
              <ChatWindow chat={selectedChat} />
            </div>

            <div style={{ padding: "10px", borderTop: "1px solid #ccc" }}>
              <input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                style={{ width: "80%", padding: "10px" }}
                placeholder="Type your message..."
              />
              <button onClick={handleSend} style={{ padding: "10px 20px", marginLeft: "10px" }}>
                Send
              </button>
            </div>
          </>
        ) : (
          <p style={{ padding: "20px" }}>Select a chat</p>
        )}
      </div>
    </div>
  );
}

export default App;
