import { useEffect, useRef } from "react";

const ChatWindow = ({ chat }) => {
  const scrollRef = useRef(null);

  useEffect(() => {
    scrollRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [chat.messages]);

  const formatTime = (isoTime) => {
    const date = new Date(isoTime);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <>
      {chat.messages.map((msg, index) => {
        const isOutgoing = msg.direction === "outgoing";
        const profilePic = msg.profilePic || "https://ui-avatars.com/api/?name=" + msg.name;

        return (
          <div key={index} className={`message-wrapper ${isOutgoing ? "you" : "other"}`}>
            {!isOutgoing && (
              <img src={profilePic} alt="User" className="avatar" />
            )}

            <div className={`message-bubble ${isOutgoing ? "you" : "other"}`}>
              <div>{msg.message}</div>
              <div className="timestamp">
                <span className="meta">
                  {formatTime(msg.timestamp)}{" "}
                  <span style={{ color: msg.status === "read" ? "blue" : "gray" }}>
                    {msg.status}
                  </span>
                </span>
              </div>
            </div>

            {isOutgoing && (
              <img src="https://ui-avatars.com/api/?name=You" alt="You" className="avatar" />
            )}
          </div>
        );
      })}
      <div ref={scrollRef} />
    </>
  );
};

export default ChatWindow;
